package day03_scannerincrementdecrement;

public class C6_IncrementDecrrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=10;
				
		//bir variablenin degerýnýn toplayarak arttýrmak ýcýn 3 yontem
		//kullanabýlýrm.
		//1.yöntem (acemi yöntemi)
				
				number = number + 5 ;
		
		  //2.yöntem ( usta iþi )
		
		number += 8 ;
		
		//4.yöntem sadece 1 arttýrmak ýcýn 
		
		number++;
		
		//bu yöntemler sadece toplama ýslemýne has degýldýr.Tüm operatörler 
		//içinde kullanýlabýlýr
		//3. yöntem sadece toplama ve cýkartma ýslemý ýcýn gecerlý carpma ve bolme gecersiz
		
	    // number'ý 6 eksýltýn 
		
		number -= 6;
		//number'ý 2 ye bolun 
		
		number /=2 ;
		
		//numberý 3 ile carp
		
		number*=3 ;
		
		//number'ý 1 eksiltin 
		
		number--;
		
		
				

	}

}
