package day03_scannerincrementdecrement;

public class C7_PreIncrementPostIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  
		
		int num =20 ;
		
		num++; // num=21
		//num þuan 21 
		
		System.out.println(num++);
		//num suan 22 oldu kanýt olarak yazdýralým,
		System.out.println(num);
		
		//once yazdýrýyor sonra arttýrýyor
		
		
		System.out.println(++num);
		//num suan 23 oldu ve yazdýrdý 
		// kýsaca printde býle ++ görürsen +1 eklenmiþtir ona 
		
		
	}

}
